$dsn = 'mysql:host=your_host;dbname=your_database;charset=utf8';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $stmt = $pdo->prepare("INSERT INTO news (title, poster, text, time) VALUES (:title, :poster, :text, :time)");\n    $stmt->execute([':title' => $_POST['newstitle'], ':poster' => '1', ':text' => $_POST['newstext'], ':time' => $time]);\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}