use Zend\Form\Form;\nuse Zend\Form\Element\Csrf;\n\nclass MyForm extends Form\n{\n    public function __construct()\n    {\n        parent::__construct('my-form');\n\n        // ... other form elements ...\n\n        $this->add([\n            'type' => Csrf::class,\n            'name' => 'csrf',\n            'options' => [\n                'message_missing_token' => 'Missing CSRF token',\n                'message_invalid_token' => 'Invalid CSRF token',\n                // Optionally define your own tokens\n                //'csrf_options' => [\n                //    'salt'     => 'your-salt',  // Optional: A unique salt string to generate the token\n                //    'timeout'  => 3600,       // Optional: How long (in seconds) you want tokens to be valid\n                //],\n            ],\n        ]);\n\n        // ... other configurations ...\n    }\n}