$fp = fopen($_SESSION['filename'], "r");\n\n// Read and skip the first line (headers)\nfgetcsv($fp, 1000, ",");\n\n// Prepare the SQL statement before entering the loop\n$stmt = mysqli_prepare($connection, "INSERT INTO csv_table(name, address, age) VALUES (?, ?, ?)");\nmysqli_stmt_bind_param($stmt, "sss", $name, $address, $age);\n\nwhile (($data = fgetcsv($fp, 1000, ",")) !== FALSE) {\n    // Assign the data to variables\n    $name = $data[0];\n    $address = $data[1];\n    $age = $data[2];\n\n    // Execute the statement\n    mysqli_stmt_execute($stmt);\n}\n\nfclose($fp);\n\n// Close the statement\nmysqli_stmt_close($stmt);