<?php\nheader('Content-Type: application/json');\n\nfunction getData() {\n    $conn = db2_connect("DRIVER={IBM DB2 ODBC DRIVER};HOSTNAME=your_host;PORT=your_port;DATABASE=your_db;", "username", "password");\n    \n    if (!$conn) {\n        die(json_encode(["error" => "Connection failed"]));\n    }\n    \n    $stmt = db2_exec($conn, "SELECT * FROM your_table");\n    $result = [];\n    while ($data = db2_fetch_assoc($stmt)) {\n        $result[] = $data;\n    }\n\n    db2_close($conn);\n    return json_encode($result);\n}\n\necho getData();\n?>