<?php\nfunction containsLuceneIndex($directory) {\n    // Check if the directory exists\n    if (!is_dir($directory)) {\n        return false;\n    }\n    \n    // Scan directory for segment files\n    $files = scandir($directory);\n    \n    // Look for segments_N file and other Lucene index files\n    $segmentPattern = '/^segments_\d+$/';\n    foreach ($files as $file) {\n        if (preg_match($segmentPattern, $file)) {\n            return true;\n        }\n    }\n\n    return false;\n}\n\n$luceneDir = '/path/to/lucene/index';\nif (containsLuceneIndex($luceneDir)) {\n    echo "Directory contains a Zend_Search_Lucene index.";\n} else {\n    echo "Directory does not contain a Zend_Search_Lucene index.";\n}\n?>