session_start();\n\nfunction enqueueTransaction($sql) {\n    if (!isset($_SESSION['transactions'])) {\n        $_SESSION['transactions'] = [];\n    }\n    $_SESSION['transactions'][] = $sql;\n}\n\nfunction commitTransactions() {\n    if (isset($_SESSION['transactions']) && count($_SESSION['transactions']) > 0) {\n        $conn = oci_pconnect('username', 'password', '//localhost/orcl');\n\n        foreach ($_SESSION['transactions'] as $sql) {\n            $stid = oci_parse($conn, $sql);\n            oci_execute($stid);\n        }\n\n        oci_commit($conn);\n        oci_close($conn);\n\n        unset($_SESSION['transactions']);\n    }\n}\n\nfunction rollbackTransactions() {\n    if (isset($_SESSION['transactions']) && count($_SESSION['transactions']) > 0) {\n        $conn = oci_pconnect('username', 'password', '//localhost/orcl');\n        oci_rollback($conn);\n        oci_close($conn);\n\n        unset($_SESSION['transactions']);\n    }\n}