// Procedural Code\nfunction get_user_info($user_id) {\n    $result = mysqli_query($connection, "SELECT * FROM users WHERE id = $user_id");\n    return mysqli_fetch_assoc($result);\n}\n\n// Object-Oriented Conversion\nclass Database {\n    protected $connection;\n\n    public function __construct($host, $username, $password, $database_name) {\n        $this->connection = new mysqli($host, $username, $password, $database_name);\n    }\n\n    public function query($sql) {\n        return $this->connection->query($sql);\n    }\n}\n\nclass User {\n    protected $db;\n\n    public function __construct(Database $db) {\n        $this->db = $db;\n    }\n\n    public function getInfo($userId) {\n        $result = $this->db->query("SELECT * FROM users WHERE id = " . intval($userId));\n        if ($result->num_rows > 0) {\n            return $result->fetch_assoc();\n        }\n        return null;\n    }\n}\n\n// Usage\n$db = new Database('localhost', 'root', '', 'test_db');\n$userModel = new User($db);\n$userInfo = $userModel->getInfo(1);