// Assuming $db is your Zend_Db_Adapter instance\n\n// Execute a SELECT query\n$select = $db->select()->from('your_table');\n$stmt = $db->query($select);\n$resultSet = $stmt->fetchAll();\n\n// Count the number of rows returned\n$rowCount = count($resultSet);\n\necho "Number of rows: " . $rowCount;\n\n// Alternatively, using fetchAll() and rowCount in a simpler way:\n$rowCountDirectly = $db->query($select)->rowCount();\necho "Number of rows directly: " . $rowCountDirectly;