<?php\n$data = array(\n    array('Area1', null, null),\n    array(null, 'Section1', null),\n    array(null, null, 'Location1'),\n    array('Area2', null, null),\n    array(null, 'Section2', null),\n    array(null, null, 'Location2')\n);\n$root = array();\nforeach ($data as $row) {\n    if ($row[0]) {\n        // Initialize a new area array for each Area\n        $area = array();\n        $root[$row[0]] =& $area;\n    } elseif ($row[1]) {\n        // Ensure the current area is initialized with sections\n        if (!isset($area)) {\n            $area = array();\n        }\n        // Initialize a new section array for each Section\n        $section = array();\n        $area[$row[1]] =& $section;\n    } elseif ($row[2]) {\n        // Ensure the current section is initialized with locations\n        if (!isset($section)) {\n            $section = array();\n        }\n        $section[] = $row[2];\n    }\n}\nprint_r($root);\n?>