// In check1()\n$score_table = array_fill_keys($all_terms, 0); // Initialize all possible keys to zero.\nforeach ($terms_in_article1 as $term) {\n    $score_table[$term]++;\n}\n$score_table2 = $score_table;\nforeach ($terms_in_article2 as $term) {\n    $score_table2[$term]++;\n}\n\n// In check2()\n$score_table = array_fill_keys(array_intersect_key($all_terms, $terms_in_article1), 0); // Only keys from article1.\nforeach ($terms_in_article1 as $term) {\n    if (isset($score_table[$term])) {  // This condition ensures only words in both texts are considered.\n        $score_table[$term]++;\n    }\n}\n$score_table2 = array_fill_keys(array_intersect_key($score_table, $terms_in_article2), 0); // Only keys from article1 present also in article2.\nforeach ($terms_in_article2 as $term) {\n    if (isset($score_table2[$term])) {  // This ensures only overlapping words are considered.\n        $score_table2[$term]++;\n    }\n}