<?php\n// Sample text with quotes\n$text = "This is a \"quoted\" string.";\n\n// Encode the text before inserting into the database\n$encodedText = htmlentities($text);\n\n// Simulate storing and fetching from the database\n$fetchedTextFromDB = $encodedText;\n\n// Decode the text after fetching from the database\n$outputText = htmlspecialchars_decode($fetchedTextFromDB, ENT_QUOTES);\n\necho $outputText; // Outputs: This is a "quoted" string.\n?>