$htmlForm = '<form id="frmlogin">';\n// Adding a hidden submit input to help with Enter key submission in IE8\n$htmlForm .= '<input type="submit" style="display:none;">';\nswitch(LOGIN_METHOD) {\n    case 'both':\n        $htmlForm .= $ACL_LANG['USERNAME'].'/'.$ACL_LANG['EMAIL'];\n        break;\n    case 'email':\n        $htmlForm .= $ACL_LANG['EMAIL'];\n        break;\n    default:\n        $htmlForm .= $ACL_LANG['USERNAME'];\n        break;\n}                       \n$htmlForm .= '<label>:';\nswitch(LOGIN_METHOD) {\n    case 'both':\n    case 'email':\n        $htmlForm .= '<input type="text" name="u" id="u" class="textfield" /></label>';\n        break;\n    default:\n        $htmlForm .= '<input type="text" name="u" id="u" class="textfield" /></label>';\n        break;\n}\n$htmlForm .= '<label>'.$ACL_LANG['PASSWORD'].':</label>'.\n             '<input type="password" name="p" id="p" class="textfield" />'.\n             '<br /><center><input type="submit" name="btn" id="btn" class="buttonfield" value="Sign in to extranet" /></center>'.\n             '</form>';\n\nreturn $htmlForm;