<?php\n\nrequire('vendor/autoload.php');\n\nuse setasign\Fpdi\Fpdi;\n\n// initiate FPDI\n$pdf = new Fpdi();\n\n// add a page from an existing PDF file\n$pageCount = $pdf->setSourceFile('document.pdf');\n$tplIdx = $pdf->importPage(1);\n$pdf->AddPage();\n$pdf->useTemplate($tplIdx);\n\n// now you can add text or other content directly onto the imported page\n$pdf->SetFont('Arial', '', 12);\n$pdf->SetXY(80, 75); // position coordinates where text will be added\n$pdf->Write(0, 'Added Text');\n\n// output the new document to file or browser\n$pdf->Output('new_document.pdf', 'F'); // write to file\n\n?>