// Start the session at the beginning of your script\nsession_start();\n\n// Check if user_id exists in session\nif (isset($_SESSION['user_id'])) {\n    // Connect to your database and check for existing session\n    $conn = new mysqli('host', 'username', 'password', 'database');\n    $stmt = $conn->prepare("SELECT session_id FROM sessions WHERE user_id=?");\n    $stmt->bind_param("i", $_SESSION['user_id']);\n    $stmt->execute();\n    $stmt->store_result();\n\n    // If another session is found, invalidate it\n    if ($stmt->num_rows > 0) {\n        $stmt = $conn->prepare("DELETE FROM sessions WHERE user_id=?");\n        $stmt->bind_param("i", $_SESSION['user_id']);\n        $stmt->execute();\n    }\n\n    // Save the new session ID in the database\n    $session_id = session_id();\n    $stmt = $conn->prepare("INSERT INTO sessions (user_id, session_id) VALUES (?, ?)");\n    $stmt->bind_param("is", $_SESSION['user_id'], $session_id);\n    $stmt->execute();\n}\n\n// Close the connection\n$conn->close();