<?php\n// Assuming $link is your mysqli_connect resource\n\n$query = "INSERT INTO your_table (column1, column2) VALUES (?, ?)";\n$stmt = $link->prepare($query);\n$stmt->bind_param("ss", 'value1', 'value2');\nif ($stmt->execute()) {\n    $lastInsertedId = $link->insert_id;\n    echo "Last inserted ID is: " . $lastInsertedId;\n} else {\n    echo "Error inserting data: " . $stmt->error;\n}\n$stmt->close();\n?>