<?php\n$mysqli = new mysqli("host", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $bug_id = intval($_POST['bug_id']);\n    $by = $_POST['by'];\n    $content = $_POST['content'];\n\n    if (!empty($content)) {\n        $stmt = $mysqli->prepare("INSERT INTO bug_replies (bug_id, by, content) VALUES (?, ?, ?)");\n        $stmt->bind_param("iss", $bug_id, $by, $content);\n        \n        if ($stmt->execute()) {\n            echo "Reply added successfully.";\n        } else {\n            echo "Error: " . $stmt->error;\n        }\n    } else {\n        echo "Content must not be empty.";\n    }\n\n    $stmt->close();\n}\n\n$mysqli->close();\n?>