<?php\n\n   // In yourbootstrap.php or wherever you configure the view\n   protected function _initView() {\n       $view = new My_View();\n       \n       // Additional setup for the view, e.g., set base path, etc.\n       $view->setBasePath('/path/to/views');\n\n       // Set the view object that will be used by controllers\n       $this->bootstrap('layout');\n       $layout = $this->_getResource('layout');\n       $layout->getView()->addHelperPath('/path/to/helpers', 'My_View_Helper');\n\n       return $view;\n   }