<?php\n$bug_id = intval($_GET['id']);\n\n// Query for the bug details\n$bug_query = $mysqli->prepare("SELECT * FROM bugs WHERE id = ?");\n$bug_query->bind_param("i", $bug_id);\n$bug_query->execute();\n$bug_result = $bug_query->get_result();\n\nif ($bug_row = $bug_result->fetch_assoc()) {\n    echo "<h2>" . htmlspecialchars($bug_row['title']) . "</h2>";\n    echo "<p><strong>Reported by:</strong> " . htmlspecialchars($bug_row['by']) . "</p>";\n    echo "<p><strong>Date and Time:</strong> " . $bug_row['datetime'] . "</p>";\n    echo "<p>" . nl2br(htmlspecialchars($bug_row['content'])) . "</p>";\n\n    // Query for the replies to this bug\n    $replies_query = $mysqli->prepare("SELECT * FROM bug_replies WHERE bug_id = ? ORDER BY datetime ASC");\n    $replies_query->bind_param("i", $bug_id);\n    $replies_query->execute();\n    $replies_result = $replies_query->get_result();\n\n    if ($replies_result->num_rows > 0) {\n        echo "<h3>Replies:</h3>";\n        while ($reply_row = $replies_result->fetch_assoc()) {\n            echo "<p><strong>" . htmlspecialchars($reply_row['by']) . " replied on " . $reply_row['datetime'] . ":</strong></p>";\n            echo "<p>" . nl2br(htmlspecialchars($reply_row['content'])) . "</p>";\n        }\n    } else {\n        echo "<p>No replies yet.</p>";\n    }\n}\n\n$bug_query->close();\n$replies_query->close();\n$mysqli->close();\n?>