<?php\n// Start session\nsession_start();\n\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $username = $_POST['username'];\n    $password = $_POST['password'];\n\n    // Dummy authentication logic\n    if ($username === 'admin' && $password === 'password') {\n        $sessionId = bin2hex(random_bytes(16));  // Secure random session ID generation\n\n        // Setting a secure cookie on login success\n        setcookie(\n            'session_id',      // Name of the cookie\n            $sessionId,        // Cookie value\n            [\n                'expires' => time() + (86400 * 30),    // Expire in 30 days\n                'path' => '/',                         // Avaivlable to all paths on domain\n                'domain' => 'yourdomain.com',          // Specific domain if needed\n                'secure' => true,                      // Send only over HTTPS\n                'httponly' => true,                    // Prevents JavaScript access via document.cookie (XSS mitigation)\n                'SameSite' => 'Strict'                  // Mitigates CSRF attacks\n            ]\n        );\n\n        $_SESSION['user'] = $username;\n        \n        echo json_encode(['success' => true, 'message' => 'Login successful']);\n    } else {\n        echo json_encode(['success' => false, 'message' => 'Invalid username or password']);\n    }\n}\n?>