<?php\n\nclass UserData {\n    private $name;\n\n    public function __construct($userId) {\n        // Simulate fetching data from a database using the userId\n        // In practice, you'd use a database query here\n        $userData = [\n            24 => ['name' => 'Bob'],\n            25 => ['name' => 'Alice'],\n            // Add more users as needed\n        ];\n\n        if (isset($userData[$userId])) {\n            $this->name = $userData[$userId]['name'];\n        } else {\n            throw new Exception("User not found");\n        }\n    }\n\n    public function getName() {\n        return $this->name;\n    }\n}\n\ntry {\n    $user24 = new UserData(24);\n    echo 'Welcome, ' . $user24->getName() . '!'; // Outputs: Welcome, Bob!\n} catch (Exception $e) {\n    echo 'Error: ' . $e->getMessage();\n}\n?>