// Assuming $db is an instance of Zend_Db_Adapter_Abstract\n\n$group_id = 55; // Your group id\n\n// Create a subquery\n$subQuerySelect = $db->select()\n                     ->from('gs_relations', 'subscriber_id')\n                     ->where('group_id = ?', $group_id);\n\n// Use the subquery in the main query\n$select = $db->select()\n              ->from('subscribers')\n              ->where_in('id', $subQuerySelect);\n\necho $select->__toString(); // This will show your SQL query\n\n// To get results, you would use:\n$results = $db->fetchAll($select);