<?php\n// Assuming $link is your MySQL connection resource obtained via mysql_connect\n\n$query = "INSERT INTO your_table (column1, column2) VALUES ('value1', 'value2')";\n$result = mysql_query($query, $link);\n\nif ($result) {\n    $lastInsertedId = mysql_insert_id($link);\n    echo "Last inserted ID is: " . $lastInsertedId;\n} else {\n    echo "Error inserting data: " . mysql_error();\n}\n?>