try {\n    $pdo = new PDO('mysql:host=localhost;dbname=test', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare and execute the statement\n    $stmt = $pdo->prepare("INSERT INTO jobs (JobName) VALUES (:jobName)");\n    $stmt->execute(['jobName' => 'Existing Job Name']);\n\n} catch (PDOException $e) {\n    if ($e->getCode() == 23000) { // SQLSTATE code for integrity constraint violation\n        echo "Integrity constraint violation: " . $e->getMessage();\n        // Handle the specific case of a duplicate entry, e.g., by logging or notifying the user.\n    } else {\n        throw $e; // Re-throw the exception if it's not a duplicate entry error\n    }\n}