<?php\n\nif (isset($_GET['user_id'])) {\n    $userId = $_GET['user_id'];\n    $content = 'user_id'; // Set to a known string that matches the switch case\n} else {\n    $userId = null;\n    $content = isset($_GET['content']) ? $_GET['content'] : 'default';\n}\n\nswitch ($content) {\n    // The default page\n    case 'default':\n        include_once('main.php');\n        break;\n\n    // The news related stuff\n    case 'news':\n        include_once('news.php');\n        break;\n\n    // Show the profile of a user\n    case 'user_id':\n        if ($userId !== null) {\n            include_once('profile.php'); // Ensure userId is used appropriately in profile.php\n        } else {\n            include_once('main.php'); // Fallback to default if no valid user_id provided\n        }\n        break;\n\n    default:\n        include_once('main.php');\n        break;\n}\n\n?>