<?php\n   // Determine language preference (for example, using a query parameter)\n   $lang = isset($_GET['lang']) ? $_GET['lang'] : 'en';\n   \n   // Load the appropriate JSON file\n   $translations_path = __DIR__ . "/languages/{$lang}.json";\n   if (file_exists($translations_path)) {\n       $translations = json_decode(file_get_contents($translations_path), true);\n   } else {\n       $translations = [];\n   }\n   ?>