function removeTemplateComments($templateContent) {\n    // Regular expression pattern to match custom comments like <%-- comment --%>\n    return preg_replace('/<%\-\-.*?\-\-%>/s', '', $templateContent);\n}\n\n// Example usage:\n$template = '<p>This is a sample paragraph.<%-- This is a template comment that should not appear in the output. --%></p>';\n$cleanTemplate = removeTemplateComments($template);\n\necho htmlspecialchars($cleanTemplate); // Outputs: <p>This is a sample paragraph.</p>