function encrypt_param($matches) {\n    // Base64 encode the captured group from the regex match\n    return 'href="somepage.php?x=' . base64_encode($matches[1]) . '"';\n}\n\n$webdoc = preg_replace_callback(\n    '/href="somepage.php\?x=([^"]+)"/',  // Regex pattern to match the URL part after x=\n    'encrypt_param',                     // Function to apply for each match\n    $webdoc                              // Input string containing HTML/URLs\n);