// Ensure default internal encoding is set to UTF-8\nmb_internal_encoding('UTF-8');\n\n$string = "Caf√©"; // String containing non-ASCII character\n\n// Use mb_strlen for proper length calculation with multi-byte characters\n$length = mb_strlen($string, 'UTF-8');\necho "Length of string: " . $length;\n\n// Use mb_substr to safely get a substring from a multi-byte string\n$substring = mb_substr($string, 0, 3, 'UTF-8');\necho "Substring: " . $substring;