<?php\nif (isset($_POST['parse'])) {\n    // Path to your Perl script\n    $perlScriptPath = '/home/www/Init.pm';\n    \n    // Command to execute the Perl script\n    $command = escapeshellcmd("perl $perlScriptPath -f games_mp.log");\n    \n    // Execute the command and capture the output\n    exec($command, $output, $return_var);\n    \n    if ($return_var === 0) {\n        echo "Parsing completed successfully.<br>";\n        foreach($output as $line) {\n            echo htmlspecialchars($line) . "<br>";\n        }\n    } else {\n        echo "Error during parsing:<br>";\n        foreach($output as $line) {\n            echo htmlspecialchars($line) . "<br>";\n        }\n    }\n}\n?>\n\n<!DOCTYPE html>\n<html lang="en">\n<head>\n    <meta charset="UTF-8">\n    <title>Log Parser</title>\n</head>\n<body>\n    <form method="post" action="">\n        <input type="submit" name="parse" value="Parse Logs">\n    </form>\n</body>\n</html>