function loadAndParseHtml($htmlContent) {\n    // Create a new DOMDocument instance\n    $dom = new DOMDocument();\n\n    // Load the HTML content into the DOMDocument\n    // @ is used to suppress warnings about malformed HTML\n    @$dom->loadHTML($htmlContent);\n\n    return $dom;\n}\n\n// Example usage\n$htmlContent = '<p>I\'m writing a PHP script that involves scraping web pages.</p><img src="example.jpg"\nalt="example">';\n\n$dom = loadAndParseHtml($htmlContent);\n\n// Iterate over all elements to demonstrate parsing\nforeach ($dom->getElementsByTagName('*') as $element) {\n    echo "Tag: {$element->tagName}\n";\n}