$stmt = $pdo->prepare("\n    SELECT e.* \n    FROM event e\n    LEFT JOIN event_location el ON e.event_id = el.event_id\n    LEFT JOIN location l ON el.location_id = l.location_id\n    WHERE (e.keywords LIKE :keyword1 OR e.keywords LIKE :keyword2)\n      AND l.some_location_field = :userLocation\n");\n$stmt->execute([':keyword1' => '%hello%', ':keyword2' => '%world%', ':userLocation' => $user_specified_location]);