class FrontController {\n    public function handleRequest($uri) {\n        if (strpos($uri, '/admin') === 0) {\n            // Load Admin Controller\n            $controller = new AdminController();\n        } elseif (strpos($uri, '/user') === 0) {\n            // Load User Controller\n            $controller = new UserController();\n        } else {\n            // Default Controller\n            $controller = new DefaultController();\n        }\n        \n        $controller->processRequest();\n    }\n}\n\ninterface IController {\n    public function processRequest();\n}\n\nclass AdminController implements IController {\n    public function processRequest() {\n        echo "Processing admin request.";\n    }\n}\n\nclass UserController implements IController {\n    public function processRequest() {\n        echo "Processing user request.";\n    }\n}\n\nclass DefaultController implements IController {\n    public function processRequest() {\n        echo "Processing default request.";\n    }\n}\n\n// Example usage\n$frontController = new FrontController();\n$frontController->handleRequest($_SERVER['REQUEST_URI']);