$connect = mysqli_connect($host, $user, $passwd);\n\nif (!$connect) {\n    die("Hoops, error! " . mysqli_error($connect));\n}\n\n// Check if the database exists and select it, or create it if it doesn't exist\n$result = mysqli_query($connect, "SHOW DATABASES LIKE '{$db}'");\n\nif (!$result || mysqli_num_rows($result) == 0) {\n    // Create database if it does not exist\n    $create_db = "CREATE DATABASE {$db}";\n    if (!mysqli_query($connect, $create_db)) {\n        die("Error creating database: " . mysqli_error($connect));\n    }\n}\n\n// Select the database\nif (!mysqli_select_db($connect, $db)) {\n    die("No database selected! Error: " . mysqli_error($connect));\n}