<?php\n$url = $_GET['fileURL']; // Ensure this is sanitized and validated to prevent security issues\n\n// Get headers from the remote file to determine content type and length\n$headers = get_headers($url, 1);\n$ctype = $headers['Content-Type'];\n$contentLength = isset($headers['Content-Length']) ? $headers['Content-Length'] : '';\n\nheader("Pragma: public");\nheader("Expires: 0");\nheader("Cache-Control: must-revalidate, post-check=0, pre-check=0");\nheader("Cache-Control: private", false);\nheader("Content-Type: " . $ctype);\nheader("Content-Disposition: attachment; filename=\"" . basename(parse_url($url, PHP_URL_PATH)) . "\";");\nheader("Content-Transfer-Encoding: binary");\nif (!empty($contentLength)) {\n    header("Content-Length: " . $contentLength);\n}\n\n// Open a stream from the URL\n$remoteHandle = fopen($url, 'r');\nif ($remoteHandle === false) {\n    die('Error opening file for reading.');\n}\n\n// Stream the file to the browser in chunks\n$chunkSize = 1024 * 8; // 8 KB chunk size\nwhile (!feof($remoteHandle)) {\n    echo fread($remoteHandle, $chunkSize);\n    ob_flush(); // Flush output buffer\n    flush(); // Flush system output buffer\n}\n\nfclose($remoteHandle);\nexit();\n?>