<?php\n\n$host = 'localhost';\n$db   = 'your_database_name';\n$user = 'your_username';\n$pass = 'your_password';\n\n// Create connection\n$conn = new mysqli($host, $user, $pass, $db);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Directory containing your HTML files\n$directory = 'path_to_your_html_files';\n\nforeach (glob("$directory/*.html") as $file) {\n    $htmlContent = file_get_contents($file);\n\n    // Load the HTML into a DOMDocument object\n    $dom = new DOMDocument;\n    @$dom->loadHTML($htmlContent);\n\n    // Extract required data using XPath\n    $xpath = new DOMXPath($dom);\n    $titleNode = $xpath->query('//div[@id="abEntryTitleText"]')->item(0);\n    $contentNode = $xpath->query('//div[@class="abEntryParagraph"]')->item(0);\n    $documentNumberNode = $xpath->query('//div[@class="abEntryDocumentNumber"]')->item(0);\n\n    $title = $dom->saveHTML($titleNode)->trim();\n    $content = $dom->saveHTML($contentNode)->trim();\n    $metadescription = $dom->saveHTML($documentNumberNode)->trim();\n\n    // Prepare and bind\n    $stmt = $conn->prepare("INSERT INTO questions (title, content, metadescription, lastupdated, related, visible, sortorder, userid, views, posvotes, negvotes, score, emailed, detectrelated, startdate, enableexpiry, expirydate, featured, workflowstatus) VALUES (?, ?, ?, NOW(), 1, 1, 0, 1, 0, 1, 0, NULL, NULL, 1, NOW(), 0, NULL, 0, 'auto_approved')");\n    $stmt->bind_param("sss", $title, $content, $metadescription);\n\n    // Execute the statement\n    if ($stmt->execute()) {\n        echo "New record created successfully\n";\n    } else {\n        echo "Error: " . $stmt->error . "\n";\n    }\n\n    // Close the statement\n    $stmt->close();\n}\n\n// Close the connection\n$conn->close();\n\n?>