function arrayToXml($array, $rootElement = 'items') {\n    $xml = new \SimpleXMLElement("<$rootElement/>");\n    arrayToSimpleXmlElement($array, $xml);\n    return $xml->saveXML();\n}\n\nfunction arrayToSimpleXmlElement(array $array, SimpleXMLElement &$node) {\n    foreach ($array as $key => $value) {\n        if (is_array($value)) {\n            // Handle nested arrays\n            $child = $node->addChild(is_string($key) ? $key : 'item');\n            arrayToSimpleXmlElement($value, $child);\n        } else {\n            // Convert boolean to string\n            $value = is_bool($value) ? ($value ? 'true' : 'false') : $value;\n            // Handle numeric keys by wrapping values in an 'item' element\n            $node->addChild(is_string($key) ? str_replace('.', '_', $key) : 'item', htmlspecialchars($value));\n        }\n    }\n}