function xmlToArray($xmlString) {\n    $xml = simplexml_load_string($xmlString);\n    return json_decode(json_encode((array)$xml), true);\n}\n\n// Example usage:\n$items = array("1", "2",\n    array(\n        "item3.1" => "3.1",\n        "item3.2" => "3.2",\n        "isawesome" => true\n    )\n);\n\n$xmlString = arrayToXml($items);\necho $xmlString;\n\n$arrayFromXml = xmlToArray($xmlString);\nprint_r($arrayFromXml);