<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Check if 'pages' is set in POST data\n    if (isset($_POST['pages'])) {\n        // If the pages are sent as a single string, explode them by commas\n        $pages = is_array($_POST['pages']) ? $_POST['pages'] : array_filter(array_map('trim', explode(',', $_POST['pages'])));\n\n        foreach ($pages as $page) {\n            if (!empty($page)) {\n                // Prepare your SQL query to insert each page into the database\n                // Using prepared statements to prevent SQL injection\n                $stmt = $pdo->prepare("INSERT INTO POSTS (page_name) VALUES (:page)");\n                $stmt->execute([':page' => $page]);\n            }\n        }\n    }\n}\n?>