<?PHP\n$last_id = 0;\necho '<table width="400">';\nwhile ($row = mysql_fetch_array($res)) {\n    // start output of new status post and comments\n    if ($row['0'] != $last_id) {\n        if ($last_id != 0) { // close the previous status post's table if it exists\n            echo '</td></tr>';\n        }\n        echo '<tr>\n                <td width="99" valign="top" style="border-bottom: 1px solid rgb(204, 204, 204); margin: 0px; padding-top: 10px; padding-right: 10px; padding-bottom: 10px; padding-left: 10px;">\n                    <div class="imageSub" style="width: 90px;"> \n                        <img class="female" src="' . htmlspecialchars($row['profileImage'], ENT_QUOTES) . '" alt="Something" width="90"/>\n                    </div>\n                </td>\n                <td width="489" style="border-bottom: 1px solid rgb(204, 204, 204); margin: 0px; padding-top: 10px; padding-right: 10px; padding-bottom: 10px; padding-left: 10px;">';\n        echo htmlspecialchars($row['status'], ENT_QUOTES) . ' <BR>';\n\n        // open the comments table\n        echo '<table width="90%" style="border: 1px solid rgb(204, 204, 204); margin: 0px; padding-top: 10px; padding-right: 10px; padding-bottom: 10px; padding-left: 10px;">';\n    }\n\n    // output comment\n    if ($row['commentid'] != '') {\n        echo '<tr>\n                <td width="14%" rowspan="2" valign="top">\n                    <img class="' . htmlspecialchars($row['gender'], ENT_QUOTES) . '" src="' . htmlspecialchars($row['commentProfileImage'], ENT_QUOTES) . '" alt="Something" width="45"/>\n                </td>\n                <td width="86%">' . htmlspecialchars($row['datePosted'], ENT_QUOTES) . '</td>\n              </tr>\n              <tr>\n                <td>' . nl2br(htmlspecialchars($row['commentText'], ENT_QUOTES)) . '</td>\n              </tr>';\n    }\n\n    $last_id = $row['0'];\n}\nif ($last_id != 0) { // close the last status post's table if it exists\n    echo '</table></td></tr>';\n}\necho '</table>';\n?>