$genres_sql = 'SELECT genreID FROM genres WHERE imdbID = ?';\n$genres = []; // Initialize an empty array to hold the genreIDs\n\nif ($stmt->prepare($genres_sql)) {\n    // bind the query parameters\n    $stmt->bind_param('i', $movieid);\n    \n    // execute the query\n    $stmt->execute();\n    \n    // store result (necessary when using bind_result with a prepared statement)\n    $stmt->store_result();\n    \n    // bind the results to variables\n    $stmt->bind_result($genre);\n    \n    // fetch values and add them to the array\n    while ($stmt->fetch()) {\n        $genres[] = $genre;\n    }\n}\n\n// Now you can echo each genre using the for loop\nif (!empty($genres)) {\n    for ($i = 0; $i < count($genres); $i++) { // Corrected condition to < instead of +1 <\n        if ($i > 0) {\n            echo ', '; // add comma and space after first item\n        }\n        echo $genres[$i];\n    }\n}