function isValidEmailThroughSMTP($email) {\n    // Extract the domain from the email address\n    list($user, $domain) = explode('@', $email, 2);\n    \n    // Check if there's a DNS MX record for the domain\n    if (!checkdnsrr($domain, 'MX')) {\n        return false;\n    }\n    \n    // For full SMTP validation, you would connect to the mail server and use SMTP commands like EHLO, MAIL FROM, RCPT TO.\n    // This is complex and typically involves a lot of code.\n    \n    // Returning true if MX record exists as a simple check\n    return true;\n}\n\n// Usage\n$email = "example@example.com";\nif (isValidEmailThroughSMTP($email)) {\n    echo "The domain has an SMTP server.";\n} else {\n    echo "No MX record found for this domain, likely invalid.";\n}