// Using PDO with prepared statements to prevent SQL Injection\n$searchTerm = $_GET['search']; // Assume this comes from user input\n\n$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n$stmt = $pdo->prepare("SELECT name, address FROM master WHERE \n                        MATCH(name, address) AGAINST(:searchTerm)");\n$stmt->execute([':searchTerm' => $searchTerm]);\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nforeach ($results as $row) {\n    echo htmlspecialchars($row['name']) . ' - ' . htmlspecialchars($row['address']);\n}