$query = "SELECT * FROM questions WHERE created_at >= DATE_SUB(NOW(), INTERVAL 7 DAY) ORDER BY votes DESC LIMIT 1";\n$result = mysqli_query($conn, $query);\n$row = mysqli_fetch_assoc($result);\n\nif ($row) {\n    echo "The best question of the week is: ". htmlspecialchars($row['title']) . "<br>";\n\n    // Display full content and other details\n} else {\n    echo "No questions were voted this week.";\n}