<?php\nsession_start();\n\n// Check if the user has the necessary privileges\nif (!isset($_SESSION['user_id']) || $_SESSION['is_member'] != 1) {\n    header('HTTP/1.0 403 Forbidden');\n    exit;\n}\n\n$video_file = 'path/to/your/video.flv';\n\n// Ensure the file exists and is readable\nif (file_exists($video_file) && is_readable($video_file)) {\n    // Set headers to force a download or stream the video\n    header('Content-Type: video/x-flv');\n    header('Content-Disposition: inline; filename="' . basename($video_file) . '"');\n    header('Content-Length: ' . filesize($video_file));\n    \n    // Output the file content\n    readfile($video_file);\n} else {\n    header('HTTP/1.0 404 Not Found');\n}\n\nexit;\n?>