function addRoundedCornersWithImageMagick($src, $dest, $radius = 12) {\n    try {\n        $image = new Imagick(realpath($src));\n        $width = $image->getImageWidth();\n        $height = $image->getImageHeight();\n\n        // Create a rounded corners mask\n        $mask = new Imagick();\n        $mask->newImage($width, $height, 'transparent');\n        $draw = new ImagickDraw();\n        $draw->roundRectangle(0, 0, $width, $height, $radius, $radius);\n        $mask->drawImage($draw);\n\n        // Apply the mask\n        $image->setImageAlphaChannel(Imagick::ALPHACHANNEL_SET);\n        $image->composeImage($mask, Imagick::COMPOSITE_DSTIN, 0, 0);\n\n        // Save the resulting image\n        $image->writeImage(realpath($dest));\n    } catch (Exception $e) {\n        echo "Error: " . $e->getMessage();\n    }\n}