include 'bazClassHere.php';\ninclude 'fooClassHere.php';\n\n$a = new Foo;\n$a->bar();\n\n// Using reflection to access the Baz instance\n$reflection = new ReflectionObject($a);\n$property = $reflection->getProperty('meh');\n$property->setAccessible(true);\n\n// Assuming 'meh' holds an instance of Baz and not just a value returned by getStuff()\n$bazInstance = $property->getValue($a);\n\n// Now you can call methods on the $bazInstance\nif ($bazInstance instanceof Baz) {\n    echo $bazInstance->fromBaz();\n} else {\n    echo "The property 'meh' does not hold an instance of Baz.";\n}