<?php \nheader('Content-Type: text/plain');\n\n$closingDate = mktime(23, 59, 59, 3, 27, 2009);\n\nfunction f1() {\n    global $closingDate;\n    return time() > $closingDate;\n}\n\nfunction f2() {\n    global $closingDate;\n    return time() < $closingDate;\n}\n\nprintf('    Time: %u\nClosing: %u\n\nt > c: %u\nf1   : %u\n\nt < c: %u\nf2   : %u', \n    time(), \n    $closingDate, \n    time() > $closingDate,\n    f1(), \n    time() < $closingDate,\n    f2());