<?php\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "yourdbname";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// SQL query to fetch data\n$sql = "SELECT id, name, amount FROM your_table";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    // Open a file for writing the IIF content\n    $fp = fopen("output.iif", "w");\n\n    // Write the header row as required by QuickBooks\n    fwrite($fp, "!TRNS\tTRNSID\tTRNSTYPE\tDATE\tACCNT\tNAME\tAMOUNT\n");\n\n    // Loop through the result set and write to IIF file\n    while($row = $result->fetch_assoc()) {\n        fwrite($fp, "TRNS\t$row[id]\tINVOICE\t2023-12-31\tAccounts Receivable\t$row[name]\t$row[amount]\n");\n    }\n\n    fclose($fp);\n} else {\n    echo "No results found";\n}\n\n$conn->close();\n?>