<?php\n// Assume $cardNumber is obtained from the client in a secure manner.\n$cardNumber = '4111111111111111';\n$salt = bin2hex(random_bytes(16));\n\n// Hashing the card number with added salt for security\n$hashedCardNumber = hash('sha256', $salt . $cardNumber);\n\n// Store the hashed card number and associated salt in your database.\n// Example: $db->query("INSERT INTO secure_cards (hash, salt) VALUES ('$hashedCardNumber', '$salt')");\n\n// When processing:\n$retrievedHash = 'previously_stored_hash';\n$savedSalt = 'previously_stored_salt';\n\nif ($hashedCardNumber == hash('sha256', $savedSalt . $cardNumber)) {\n    echo "Credit card data matches.";\n} else {\n    echo "Mismatch!";\n}\n\n?>