<?php\nif (isset($_GET['job_no'])) {\n    $jobNo = intval($_GET['job_no']); // Sanitize input\n\n    // Database connection setup here\n    // Example: $conn = new mysqli($servername, $username, $password, $dbname);\n\n    // Query to fetch data from database based on jobNo\n    $sql = "SELECT * FROM your_table WHERE jobNumber = ?";\n    $stmt = $conn->prepare($sql);\n    $stmt->bind_param("i", $jobNo);\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    // Process XML and MySQL data to display in new window\n    while ($row = $result->fetch_assoc()) {\n        echo "<p>Job ID: " . htmlspecialchars($row['job_id'], ENT_QUOTES, 'UTF-8') . "</p>";\n        echo "<p>Title: " . htmlspecialchars($row['title'], ENT_QUOTES, 'UTF-8') . "</p>";\n        // Add more fields as needed\n    }\n\n    $stmt->close();\n} else {\n    echo "No job number provided.";\n}\n?>